import React from 'react';
import { Link } from 'react-router-dom';

function AgraTourPackages() {
  const tourPackages = [
    {
      name: 'Taj Mahal & Agra Fort Tour',
      description:
        'Explore the iconic Taj Mahal and the historic Agra Fort, two UNESCO World Heritage Sites in Agra. Learn about their history and architectural beauty.',
      price: '₹10,000',
      link: '/taj-mahal-agra-fort-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Fatehpur Sikri Day Tour',
      description:
        'Discover the abandoned city of Fatehpur Sikri, a UNESCO World Heritage Site. Explore its historical palaces, mosques, and the stunning Buland Darwaza.',
      price: '₹12,000',
      link: '/fatehpur-sikri-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Sunrise Taj Mahal Tour',
      description:
        'Experience the majestic Taj Mahal at sunrise. Witness the changing colors of this white marble wonder as the sun rises behind it.',
      price: '₹8,500',
      link: '/sunrise-taj-mahal-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Agra & Mathura Tour',
      description:
        'Combine the beauty of Agra with the spiritual significance of Mathura, the birthplace of Lord Krishna. Visit temples, ghats, and the Taj Mahal.',
      price: '₹14,000',
      link: '/agra-mathura-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto bg-white p-6 rounded-lg shadow-lg"> <br />
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-8">
          Agra Tour Packages
        </h1>

        <p className="text-lg text-gray-700 text-center mb-6">
          Explore the historic city of Agra with our exclusive tour packages. Visit the Taj Mahal, Agra Fort, Fatehpur Sikri, and more. Experience the rich cultural heritage of Agra.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {tourPackages.map((tour, index) => (
            <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img
                src={tour.image}
                alt={tour.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800">{tour.name}</h2>
                <p className="text-gray-600 mt-2">{tour.description}</p>
                <p className="text-lg font-bold text-gray-900 mt-4">{tour.price}</p>
                <div className="mt-4">
                  <Link
                    to={tour.link}
                    className="text-center text-white bg-blue-600 hover:bg-blue-700 py-2 px-4 rounded-md w-full"
                  >
                    Book Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-8 text-center">
          <p className="text-gray-600">
            For more details or customizations, feel free to{' '}
            <a href="mailto:support@bookmyshow.com" className="text-blue-600">
              email us
            </a>{' '}
            or call us at{' '}
            <a href="tel:+917302778405" className="text-blue-600">
              +91 7302778405
            </a>.
          </p>
        </div>
      </div>
    </div>
  );
}

export default AgraTourPackages;
