import React from 'react';
import { Link } from 'react-router-dom';

function DelhiTourPackages() {
  const tourPackages = [
    {
      name: 'Delhi City Tour',
      description:
        'Explore the historical and cultural landmarks of Delhi, including the Red Fort, Qutub Minar, India Gate, and Humayun’s Tomb. Experience the essence of Delhi’s rich heritage.',
      price: '₹6,000',
      link: '/delhi-city-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Old Delhi Heritage Walk',
      description:
        'Walk through the narrow lanes of Old Delhi, visit Jama Masjid, Chandni Chowk, Raj Ghat, and explore the bustling markets and heritage sites of this historic area.',
      price: '₹4,500',
      link: '/old-delhi-heritage-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Delhi and Agra Tour',
      description:
        'Combine Delhi’s rich history with the beauty of Agra. Visit iconic landmarks like the Red Fort, Qutub Minar, Taj Mahal, and Agra Fort in this unforgettable tour.',
      price: '₹12,000',
      link: '/delhi-agra-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Delhi Night Tour',
      description:
        'Experience the charm of Delhi at night. Visit India Gate, Lotus Temple, and other beautifully lit monuments in the evening, while learning about the city’s night-time charm.',
      price: '₹7,500',
      link: '/delhi-night-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto bg-white p-6 rounded-lg shadow-lg"> <br />
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-8">
          Delhi Tour Packages
        </h1>

        <p className="text-lg text-gray-700 text-center mb-6">
          Explore the vibrant city of Delhi with our exclusive tour packages. From historical landmarks to modern attractions, Delhi has something for everyone.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {tourPackages.map((tour, index) => (
            <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img
                src={tour.image}
                alt={tour.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800">{tour.name}</h2>
                <p className="text-gray-600 mt-2">{tour.description}</p>
                <p className="text-lg font-bold text-gray-900 mt-4">{tour.price}</p>
                <div className="mt-4">
                  <Link
                    to={tour.link}
                    className="text-center text-white bg-blue-600 hover:bg-blue-700 py-2 px-4 rounded-md w-full"
                  >
                    Book Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-8 text-center">
          <p className="text-gray-600">
            For more details or customizations, feel free to{' '}
            <a href="mailto:support@bookmyshow.com" className="text-blue-600">
              email us
            </a>{' '}
            or call us at{' '}
            <a href="tel:+917302778405" className="text-blue-600">
              +91 7302778405
            </a>.
          </p>
        </div>
      </div>
    </div>
  );
}

export default DelhiTourPackages;
