import React from 'react';
import { Link } from 'react-router-dom';

function HimachalPradeshTours() {
  const tourPackages = [
    {
      name: 'Shimla Tour',
      description:
        'Enjoy the scenic beauty of Shimla, the Queen of Hills. Explore its colonial architecture, lush green valleys, and snowy peaks.',
      price: '₹15,000',
      link: '/shimla-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Manali Tour',
      description:
        'Experience the beauty of Manali with its snow-capped mountains, adventure activities, and vibrant culture. Ideal for both nature lovers and adventure seekers.',
      price: '₹20,000',
      link: '/manali-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Dharamshala Tour',
      description:
        'Discover the spiritual serenity of Dharamshala, the home of the Dalai Lama. Perfect for a peaceful retreat amidst the Himalayas.',
      price: '₹18,000',
      link: '/dharamshala-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
    {
      name: 'Kullu Valley Tour',
      description:
        'Explore the stunning Kullu Valley with its rivers, trekking spots, and cultural festivals. A perfect destination for adventure and nature lovers.',
      price: '₹22,000',
      link: '/kullu-valley-tour',
      image: 'data:image/jpeg;base64,', // Replace with actual image URL
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto bg-white p-6 rounded-lg shadow-lg"> <br />
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-8">
          Himachal Pradesh Tour Packages
        </h1>

        <p className="text-lg text-gray-700 text-center mb-6">
          Explore the beauty of Himachal Pradesh with our exclusive tour packages. Whether you seek adventure, spirituality, or relaxation, Himachal has something for everyone.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {tourPackages.map((tour, index) => (
            <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img
                src={tour.image}
                alt={tour.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800">{tour.name}</h2>
                <p className="text-gray-600 mt-2">{tour.description}</p>
                <p className="text-lg font-bold text-gray-900 mt-4">{tour.price}</p>
                <div className="mt-4">
                  <Link
                    to={tour.link}
                    className="text-center text-white bg-blue-600 hover:bg-blue-700 py-2 px-4 rounded-md w-full"
                  >
                    Book Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-8 text-center">
          <p className="text-gray-600">
            For more details or customizations, feel free to{' '}
            <a href="mailto:support@bookmyshow.com" className="text-blue-600">
              email us
            </a>{' '}
            or call us at{' '}
            <a href="tel:+917302778405" className="text-blue-600">
              +91 7302778405
            </a>.
          </p>
        </div>
      </div>
    </div>
  );
}

export default HimachalPradeshTours;
