import { Link } from "react-router-dom";

function Home() {
  return (
    <div>
      {/* Hero Section */}
      <section className="relative h-screen bg-cover bg-center flex items-center justify-center text-white text-center"
        style={{ backgroundImage: "url('https://images.pexels.com/photos/1000445/pexels-photo-1000445.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500')" }}>
        <div className="bg-black bg-opacity-50 p-8 rounded-lg">
          <h1 className="text-4xl font-bold mb-4">Discover Amazing Destinations</h1>
          <p className="text-lg mb-6">Plan your perfect trip with Book My Tours India</p>
          <Link to="/packages" className="bg-yellow-500 text-white px-6 py-3 rounded-lg text-lg hover:bg-yellow-600 transition">
            Explore Packages
          </Link>
        </div>
      </section>
      
      {/* Search Bar */}
      <section className="bg-gray-100 py-6 text-center">
        <input type="text" placeholder="Search for destinations..." className="w-2/3 px-4 py-2 border rounded-lg" />
        <button className="bg-blue-600 text-white px-4 py-2 ml-2 rounded-lg hover:bg-blue-700">Search</button>
      </section>
      
      {/* Featured Tour Packages */}
      <section className="max-w-screen-xl mx-auto py-12">
        <h2 className="text-3xl font-bold text-center mb-8">Popular Tour Packages</h2>
        <div className="grid md:grid-cols-3 gap-6">
          <div className="bg-white shadow-lg p-4 rounded-lg text-center">
            <img src="data:image/jpeg;base64," alt="Taj Mahal" className="w-full rounded-lg mb-4" />
            <h3 className="text-xl font-bold">Taj Mahal Tour</h3>
            <p className="text-gray-600">Explore the beauty of the Taj Mahal with a guided tour.</p>
            <Link to="/tajmahal" className="block mt-4 text-blue-600 hover:underline">View Details</Link>
          </div>
          <div className="bg-white shadow-lg p-4 rounded-lg text-center">
            <img src="data:image/jpeg;base64," alt="Rajasthan" className="w-full rounded-lg mb-4" />
            <h3 className="text-xl font-bold">Rajasthan Heritage Tour</h3>
            <p className="text-gray-600">Discover the royal heritage of Rajasthan.</p>
            <Link to="/rajasthan" className="block mt-4 text-blue-600 hover:underline">View Details</Link>
          </div>
          <div className="bg-white shadow-lg p-4 rounded-lg text-center">
            <img src="data:image/jpeg;base64," alt="Kerala" className="w-full rounded-lg mb-4" />
            <h3 className="text-xl font-bold">Kerala Backwaters</h3>
            <p className="text-gray-600">Enjoy a peaceful houseboat experience in Kerala.</p>
            <Link to="/kerala" className="block mt-4 text-blue-600 hover:underline">View Details</Link>
          </div>
        </div>
      </section>
      
      {/* Why Choose Us */}
      <section className="bg-blue-600 text-white text-center py-12">
        <h2 className="text-3xl font-bold">Why Choose Us?</h2>
        <p className="text-lg mt-4">Best travel experiences, affordable prices, and trusted guides.</p>
      </section>
      
      {/* Testimonials */}
      <section className="max-w-screen-xl mx-auto py-12 text-center">
        <h2 className="text-3xl font-bold mb-6">What Our Customers Say</h2>
        <div className="bg-gray-100 p-6 rounded-lg shadow-lg">
          <p className="text-lg italic">"Amazing experience! Everything was well organized and smooth."</p>
          <p className="mt-2 font-bold">- A Happy Traveler</p>
        </div>
      </section>
    </div>
  );
}

export default Home;
